mapper.select:
/*kr.happy.jobkorea.system.dao.ComnCodDao.listComnGrpCod*/ SELECT qa.seq seq ,qa.name name ,qa.hire_date hire_date ,qa.resign_date resign_date ,qa.cop_name cop_name ,qa.work_yn work_yn FROM tb_employment qa, tb_userinfo ui WHERE 1=1 AND qa.loginID = ui.loginID
AND qa.hire_date >= STR_TO_DATE(#{startdate}, '%Y-%m-%d')
AND qa.resign_date <= STR_TO_DATE(#{enddate}, '%Y-%m-%d')
AND qa.cop_name like concat('%',#{searchword},'%')
ORDER BY qa.seq desc LIMIT #{pageIndex}, #{pageSize}

mapper.insert:
insert into
	tb_employment(
	seq
	,loginID
	,name
	,hire_date

	,resign_date

	,cop_name
	,work_yn
	,date
)
values(
	(SELECT IFNULL(MAX(seq) + 1, 1) FROM tb_employment e)
	,#{loginID_save}
	,#{name_save}
	,#{hire_date_save}

	,#{resign_date_save}

	,#{cop_name_save}
	,#{work_yn_save}
	,now()
)

mapper.update:
update
	tb_employment
set
	hire_date=#{hire_date}

	,resign_date=#{resign_date}


	,resign_date=null

	,cop_name=#{cop_name}
	,work_yn=#{work_yn}
where
	seq=#{seq}
