mapper.select:
/*kr.happy.jobkorea.hla.dao.UserInfoDao.SelectUserInfo*/ SELECT name, loginID, password, email, tel1, tel2, tel3, joinDate, area, birthday, user_type, sex, imgname, imgpath, imgsize FROM tb_userinfo
AND name like concat('%',#{UserName},'%')
AND user_type = #{user_type}
AND DATE_FORMAT(joinDate,'%Y%m%d') >= REPLACE( #{fromDt} , '-', '')
AND DATE_FORMAT(joinDate,'%Y%m%d') <= REPLACE( #{toDt}, '-', '')
ORDER BY joinDate DESC LIMIT #{pageIndex}, #{pageSize}

mapper.insert:
/*kr.happy.jobkorea.hla.dao.UserInfoDao.RegistUser*/		 
INSERT INTO tb_userinfo
(
	loginID,
	user_type,
	name,
	password,
	email,
	sex,
	tel1,
	tel2,
	tel3,
	joinDate,
	area,
	birthday
)
VALUES
(
		#{loginID}
	,	#{user_type}			
	,	#{name}
	,	#{password}
	,	#{email}
	,	#{sex}
	,	#{tel1}
	,	#{tel2}
	,	#{tel3}
	,	NOW()
	,	#{area}
	,	REPLACE( #{birthday} , '-', '')	
)

mapper.update:
/*kr.happy.jobkorea.hla.dao.UserInfoDao.UpdateUser*/ UPDATE tb_userinfo SET name = #{name}, password = #{password}, email = #{email}, sex = #{sex}, tel1 = #{tel1}, tel2 = #{tel2}, tel3 = #{tel3}, area = #{area}, birthday = REPLACE( #{birthday} , '-', '')
,imgname = #{fileName} ,imgpath = #{filePath} ,imgsize = #{fileSize}
WHERE loginID = #{loginID}

mapper.select:
/*kr.happy.jobkorea.hla.dao.UserInfoDao.SelectLectureList*/ SELECT tl.no, tl.title, tl.startdate, tl.enddate, (SELECT COUNT(*) FROM tb_tcourse tc WHERE tl.no = tc.no ) AS students
FROM tb_lecture tl
AND tl.title like concat('%',#{LectureName},'%')
LIMIT #{pageIndex}, #{pageSize}

/*kr.happy.jobkorea.hla.dao.UserInfoDao.SelectStudentList*/
SELECT
	ui.loginID,
	ui.name,
	ui.birthday
FROM tb_userinfo ui , tb_tcourse tc

		ui.loginID = tc.loginID
		AND tc.no = #{LectureNo}

		ORDER BY joinDate DESC
      	LIMIT #{pageIndex}, #{pageSize}

/*kr.happy.jobkorea.hla.dao.UserInfoDao.getStudentCnt*/
SELECT
	COUNT(*) AS SelectedCnt
FROM tb_userinfo ui , tb_tcourse tc

		ui.loginID = tc.loginID
		AND tc.no = #{LectureNo}

/*kr.happy.jobkorea.hla.dao.UserInfoDao.ResumeTestList*/
SELECT
	loginID
FROM tb_userinfo
WHERE
	loginID = #{loginID}
