mapper.select:
select ts.no, ts.seq, ts.loginID, ts.nt_title, DATE_FORMAT(ts.reg_date, '%Y-%m-%d') as reg_date, ts.filename from tb_task ts left outer join tb_lecture le on ts.no = le.no where le.loginID = #{sessionVal}
and ts.loginID like '%${searchword}%'
and ts.nt_title like '%${searchword}%'
ORDER BY ts.no LIMIT #{pageIndex}, #{pageSize}

SELECT no, nt_title, nt_note, filename, filepath, filesize, reg_date, loginID
FROM tb_task 
where no=#{no}
