mapper.select:
SELECT nt_no, nt_title, nt_note, DATE_FORMAT(write_date, '%y-%m-%d') as write_date ,loginID, filename, filepath, filesize FROM tb_notice where loginID like #{sessionVal}
and nt_title like '%${searchword}%'
and nt_note like '%${searchword}%'
and write_date >= DATE_FORMAT('#{fromDt}','%y-%m-%d')
and write_date <= DATE_FORMAT('#{toDt}','%y-%m-%d')
ORDER BY nt_no LIMIT #{pageIndex}, #{pageSize}

mapper.update:

	update tb_notice SET
	  nt_title = #{nt_title}
	  ,nt_note = #{nt_note}
	  ,write_date = DATE_FORMAT(NOW(),'%Y-%c-%d')   
	  
		  ,filename = #{fileInfo.filename}
		  ,filepath = #{fileInfo.filepath}
		  ,filesize = #{fileInfo.filesize}		  
	  
	WHERE nt_no = #{nt_no}
