mapper.select:
select user.loginID, user.name, user.user_position, dtl.detail_name as area, dtl2.detail_name as skill, user.skill_add from tb_userinfo as user join tb_detail_code as dtl on user.area = dtl.detail_code and group_code = 'areaCD' join tb_detail_code as dtl2 on user.skill = dtl2.detail_code and dtl2.group_code = 'SKLCD' where user.user_type = 'D' AND user.USE_YN = '0'
and user.area = #{searcharea}
and user.user_position = #{searchind}
and user.skill = #{searchlevel}
group by user.loginID ORDER BY user.name LIMIT #{pageIndex}, #{pageSize}

		SELECT COUNT(*) AS tot_cnt 
		 from   tb_userinfo	 		
		where   user_type = 'D'	
		  AND   USE_YN = '0'		

   			 	and area = #{searcharea}


   			 	and user_position = #{searchind}


   			 	and skill = #{searchlevel}

mapper.insert:
/*kr.happy.jobkorea.jmli.dao.JmliDeveloperDao.insertMessage*/		

