mapper.update:
update tb_userinfo

SET
	password = #{password}
	,email = #{email}
  	,email_cop = #{email_cop}
  	,birthday = #{birthday}
  	,comp_name = #{comp_name}
  	,mgr_name = #{mgr_name}
  	,mgr_tel1 = #{mgr_tel1}
  	,mgr_tel2 = #{mgr_tel2}
  	,mgr_tel3 = #{mgr_tel3}
  	,comp_info = #{comp_info}

WHERE loginID = #{loginID}
