mapper.select:
/*kr.happy.jobkorea.jmyp.dao.JMessageDao.messageList*/ SELECT ms.msg_code, ms.msg_note, ms.msg_date, ms.msg_receive, ms.msg_send, ui.name as receive_name, ui2.name as send_name, ( CASE ms.msg_receive WHEN #{loginId} THEN 'r' ELSE 's' END )AS msgtype FROM TB_MESSAGE ms, tb_userinfo ui, tb_userinfo ui2
ms.msg_receive = ui.loginID AND ms.msg_send = ui2.loginID
AND ( ui.name like concat('%',#{UserName},'%') OR ui2.name like concat('%',#{UserName},'%') )
AND DATE_FORMAT(ms.msg_date,'%Y%m%d') >= REPLACE( #{fromDt} , '-', '')
AND DATE_FORMAT(ms.msg_date,'%Y%m%d') <= REPLACE( #{toDt}, '-', '')
AND (ms.msg_receive = #{loginId} OR ms.msg_send = #{loginId})
AND ms.msg_receive = #{loginId}
AND ms.msg_send = #{loginId}
ORDER BY ms.msg_date DESC LIMIT #{pageIndex}, #{pageSize}

/*kr.happy.jobkorea.jmyp.dao.JMessageDao.getSelectedCnt*/ SELECT COUNT(*) AS SelectedCnt FROM TB_MESSAGE ms, tb_userinfo ui, tb_userinfo ui2
ms.msg_receive = ui.loginID AND ms.msg_send = ui2.loginID
AND ( ui.name like concat('%',#{UserName},'%') OR ui2.name like concat('%',#{UserName},'%') )
AND DATE_FORMAT(ms.msg_date,'%Y%m%d') >= REPLACE( #{fromDt} , '-', '')
AND DATE_FORMAT(ms.msg_date,'%Y%m%d') <= REPLACE( #{toDt}, '-', '')
AND (ms.msg_receive = #{loginId} OR ms.msg_send = #{loginId})
AND ms.msg_receive = #{loginId}
AND ms.msg_send = #{loginId}

mapper.insert:
/*kr.happy.jobkorea.jmyp.dao.JMessageDao.insertMessage*/		

	SELECT IFNULL(MAX(CAST(msg_code AS UNSIGNED)),0) + 1 FROM TB_MESSAGE 

INSERT INTO TB_MESSAGE
(
		msg_code, msg_note, msg_date, msg_receive, msg_send
)
VALUES
(
		#{msg_seq}
	,	#{msg_note}
	,	NOW()
	,	#{msg_receive}
	,	#{loginId}			
)

mapper.select:
/*kr.happy.jobkorea.jmyp.dao.JMessageDao.SearchID*/ SELECT name, loginID FROM tb_userinfo
name like concat('%',#{SearchName},'%')
LIMIT #{pageIndex}, #{pageSize}
