mapper.insert:
INSERT INTO tb_task
(
 no, 
 seq,
 loginID, 
 nt_title, 
 nt_note,
 filename, 
 filepath, 
 filesize,
 reg_date
 )
 
SELECT 
#{nt_no}
,ifnull(MAX(seq)+1,1)
,#{loginId}
,#{nt_title}
,#{nt_note}
,#{fileInfo.filename}
,#{fileInfo.filepath}
,#{fileInfo.filesize}
,DATE_FORMAT(NOW(),'%Y-%c-%d %T')
FROM tb_task

mapper.update:
UPDATE tb_task 
SET nt_title = #{nt_title}
   ,nt_note = #{nt_note}
   ,reg_date = DATE_FORMAT(NOW(),'%Y-%c-%d')
   

	,filename = #{fileInfo.filename}
	,filepath = #{fileInfo.filepath}
	,filesize = #{fileInfo.filesize}

WHERE no = #{nt_no}

mapper.select:
select no
from tb_tcourse
where loginID = #{loginId}
